import React from "react";

import {
    Form,
    Select,
    InputNumber,
    Switch,
    Radio,
    Slider,
    Button,
    Upload,
    Icon,
    Rate,
    Checkbox,
    Row,
    Col,
    Card,
} from 'antd';

let responseData={
    url:'https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png'
};

const { Option } = Select;
const { Meta } = Card;

class Demo extends React.Component {
    //提交事件
    //图片单独提交，submit只是为了请求后台数据
    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
            let data = new FormData();
            data.append("upload",values.upload);
            let url = "";
            axios.post(url, data,{
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            })
                .then(function (response) {
                    //得到后台返回的分数值
                    responseData=response.data;
                    console.log(responseData.score);
                    var html = "";
                    html +="<span>测试："+responseData.score+"</span>";
                })
                .catch(function (error) {
                    console.log(error);
                });
        });
    };

    //图片预览
    handlePreview = async file => {
        if (!file.url && !file.preview) {
            file.preview = await getBase64(file.originFileObj);
        }

        this.setState({
            previewImage: file.url || file.preview,
            previewVisible: true,
        });
    };

    //关闭预览框
    handleCancel = () => this.setState({ previewVisible: false });

    handleChange = ({ fileList }) => this.setState({ fileList });

    normFile = e => {
        console.log('Upload event:', e);
        if (Array.isArray(e)) {
            return e;
        }
        return e && e.fileList;
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: { span: 6 },
            wrapperCol: { span: 14 },
        };
        return (
            <Form {...formItemLayout} onSubmit={this.handleSubmit}>
                <Form.Item label="上传图片">
                    {getFieldDecorator('dragger', {
                        valuePropName: 'fileList',
                        getValueFromEvent: this.normFile,
                    })(
                        <Upload.Dragger name="files" action="/upload.do">
                            <p className="ant-upload-drag-icon">
                                <Icon type="inbox" />
                            </p>
                            <p className="ant-upload-text">Click or drag file to this area to upload</p>
                        </Upload.Dragger>,
                    )}
                </Form.Item>

                <Form.Item wrapperCol={{ span: 12, offset: 6 }}>
                    <Button type="primary" htmlType="submit">
                        Submit
                    </Button>
                </Form.Item>
                <Card
                    hoverable
                    style={{ width: 240,marginLeft:550}}
                    cover={<img alt="example" src={responseData.url} />}
                >
                    <Meta title="处理结果" style={{textAlign:"center"}}/>
                </Card>

            </Form>
        );
    }
}

const WrappedDemo1 = Form.create({ name: 'validate_other' })(Demo);
export default WrappedDemo1;
