import React from "react";
import axios from 'axios';
import reqwest from 'reqwest';

import {
    Form,
    Select,
    InputNumber,
    Switch,
    Radio,
    Slider,
    Button,
    Upload,
    Icon,
    Rate,
    Checkbox,
    Row,
    Col,
    Card,
    Modal,
    message,
} from 'antd';

//定义后台返回数据类型
let responseData={
    score:'',
    root:'',
    baike_info:{},
    keyword:''
};

let img={
    imgUrl:''
};

const { Option } = Select;
const { Meta } = Card;

function getBase64(file) {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });
}

function load() {
    document.getElementById("p1").innerHTML = responseData.messageDetail.image_detial.score;
    document.getElementById("img1").src = "http://couseraccess.oss-cn-beijing.aliyuncs.com/Magic%20Deer.jpg";
}

class Demo extends React.Component {
    state = {
        loading: false,
    };

    handleClick() {
        img.imgUrl='';
        alert(img.imgUrl);
    };

    handleChange = ({ fileList }) => this.setState({ fileList });

    render() {

        const uploadButton = (
            <div>
                <Icon type={this.state.loading ? 'loading' : 'plus'}/>
                <div className="ant-upload-text">上传</div>
            </div>
        );
        const { imageUrl } = this.state;

        const props = {
            name: "avatar",
            showUploadList: false,//设置只上传一张图片，根据实际情况修改
            customRequest: info => {//手动上传
                const formData = new FormData();
                formData.append('filename', info.file);//名字和后端接口名字对应
                formData.append('recognize_type', "1");
                reqwest({
                    url: 'http://www.icube.fun:8000/image_recognize/',//上传url
                    method: 'post',
                    processData: false,
                    data: formData,
                    success: (res) => {//上传成功回调
                        message.success("success");
                        var data = JSON.parse(res);
                        console.log(data);
                        responseData = data;
                        console.log("------");
                        load();
                        if (data.statusCode === 0) {
                            this.setState({
                                imageUrl: res.imageUrl,
                            });
                            alert(1111);
                            message.success('上传成功！');
                            responseData = data;
                        }
                    },
                    error: () => {//上传失败回调
                        message.error('上传失败！');
                    },
                });
            },
            onRemove: file => {//删除图片调用
                this.setState(state => {
                    const index = state.fileList.indexOf(file);
                    const newFileList = state.fileList.slice();
                    newFileList.splice(index, 1);
                    return {
                        fileList: newFileList,
                    };
                });
            },
            listType: "picture-card",
            className: "avatar-uploader",

            beforeUpload: file => {//控制上传图片格式
                const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';

                if (!isJpgOrPng) {
                    message.error('您只能上传JPG/PNG 文件!');
                    return;
                }
                const isLt2M = file.size / 1024 / 1024 < 2;
                if (!isLt2M) {
                    message.error('图片大小必须小于2MB!');
                    return;
                }
                return isJpgOrPng && isLt2M;
            },
        };
        return (
            <div style={{marginLeft:600}}>
                <Upload
                    {...props}
                    listType="picture-card"
                    onChange={this.handleChange}
                >
                    {/*{img.imgUrl ? <img src={img.imgUrl} alt="avatar" style={{ width: '100%'}} /> :     uploadButton}*/}
                    <img id="img1" src={imageUrl} alt="上传图片" style={{ width: '100%'}} />
                </Upload>
                {/*<Button type="primary"*/}
                {/*    onClick={this.handleClick}*/}
                {/*>*/}
                {/*    我要上传！*/}
                {/*</Button>*/}
                <Card title="颜值打分" bordered={false} style={{ width: 300 ,marginLeft:-100,background: '#FAFAFA',textAlign:"center"}}>
                    <p id="p1"></p>
                </Card>
            </div>
        )
    }


}

const WrappedDemo = Form.create({ name: 'validate_other' })(Demo);
export default WrappedDemo;
