import React from "react";
import axios from 'axios';
//import apis from '@/services/api';
import reqwest from 'reqwest';

import {
    Form,
    Select,
    InputNumber,
    Switch,
    Radio,
    Slider,
    Button,
    Upload,
    Icon,
    Rate,
    Checkbox,
    Row,
    Col,
    Card,
    Modal,
    message,
} from 'antd';

//定义后台返回数据类型
let responseData={
    tag:'11'
};

const { Option } = Select;
const { Meta } = Card;

function getBase64(file) {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });
}

class Demo extends React.Component {
    state = {
        previewVisible: false,
        previewImage: '',
        fileList: [

        ],
        key: '',
        url: '',
        imageUrl: '',
    };

    //提交事件
    //图片单独提交，submit只是为了请求后台数据
    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
            let data = new FormData();
            data.append("upload",values.upload);
            let url = "";
            axios.post(url, data,{
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            })
                .then(function (response) {
                    //得到后台返回的分数值
                    responseData=response.data;
                    console.log(responseData.tag);
                })
                .catch(function (error) {
                    console.log(error);
                });
        });
    };

    //图片预览
    handlePreview = async file => {
        if (!file.url && !file.preview) {
            file.preview = await getBase64(file.originFileObj);
        }

        this.setState({
            previewImage: file.url || file.preview,
            previewVisible: true,
        });
    };

    //关闭预览框
    handleCancel = () => this.setState({ previewVisible: false });

    handleChange = ({ fileList }) => this.setState({ fileList });

    normFile = e => {
        console.log('Upload event:', e);
        if (Array.isArray(e)) {
            return e;
        }
        return e && e.fileList;
    };

    // 获取上传凭证
    fetchUploadToken = async () => {
        const params = {
            quantity: 1,
            module: 3,
            fileType: 1,
        };
        const res = await apis.fileSign(params);
        const { d, m } = res;
        if (m === 'success') {
            const { key, url } = d.l[0];
            this.setState({ key, url });
            return true;
        } else {
            return false;
        }
    };



    render() {
        const { previewVisible, previewImage, fileList } = this.state;
        const { imageUrl, url, key } = this.state;
        const that = this;
        const { getFieldDecorator } = this.props.form;
        const uploadButton = (
            <div>
                <Icon type="plus" />
                <div className="ant-upload-text">Upload</div>
            </div>
        );
        const formItemLayout = {
            labelCol: { span: 6 },
            wrapperCol: { span: 14 },
        };

        const props = {
            name: "avatar",
            showUploadList: false,//设置只上传一张图片，根据实际情况修改
            customRequest: info => {//手动上传
                const formData = new FormData();
                formData.append('avatar', info.file);//名字和后端接口名字对应
                reqwest({
                    url: 'http://www.icube.fun:8000/image_recognize/',//上传url
                    method: 'post',
                    processData: false,
                    data: formData,
                    success: (res) => {//上传成功回调
                        if (res.code === '0') {
                            this.setState({
                                imageUrl: res.imageUrl
                            });
                            message.success('上传成功！');
                        }
                    },
                    error: () => {//上传失败回调
                        message.error('上传失败！');
                    },
                });
            },
            onRemove: file => {//删除图片调用
                this.setState(state => {
                    const index = state.fileList.indexOf(file);
                    const newFileList = state.fileList.slice();
                    newFileList.splice(index, 1);
                    return {
                        fileList: newFileList,
                    };
                });
            },
            listType: "picture-card",
            className: "avatar-uploader",

            beforeUpload: file => {//控制上传图片格式
                const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';

                if (!isJpgOrPng) {
                    message.error('您只能上传JPG/PNG 文件!');
                    return;
                }
                const isLt2M = file.size / 1024 / 1024 < 2;
                if (!isLt2M) {
                    message.error('图片大小必须小于2MB!');
                    return;
                }
                return isJpgOrPng && isLt2M;
            },
        };

        return (
            <Form {...formItemLayout} onSubmit={this.handleSubmit}>
                <Form.Item label="选择图片">
                    {
                        getFieldDecorator('upload',{
                            valuePropName: 'fileList',
                            getValueFromEvent: this.normFile,
                        })(
                            <Upload
                                {...uploadProps}
                                // action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                //action="http://www.icube.fun:8000/image_recognize/"
                                action="http://localhost:8080/oss/fileObj"
                                listType="picture-card"
                                fileList={fileList}
                                customRequest={this.customRequest}
                                onPreview={this.handlePreview}
                                onChange={this.handleChange}
                            >
                                {fileList.length >= 1 ? null : uploadButton}
                            </Upload>
                        )
                    }
                    <Modal visible={previewVisible} footer={null} onCancel={this.handleCancel}>
                        <img alt="example" style={{ width: '100%' }} src={previewImage} />
                    </Modal>
                </Form.Item>

                <Form.Item wrapperCol={{ span: 12, offset: 6 }}>
                    <Button type="primary" htmlType="submit">
                        开始！
                    </Button>
                </Form.Item>
                <div style={{ background: '#ECECEC', padding: '30px' }}>
                    <Card title="这张图片的标签为" bordered={false} style={{ width: 300 ,marginLeft:500}}>
                        <p>{responseData.tag}</p>
                    </Card>
                </div>
            </Form>
        );
    }
}

const WrappedDemo2 = Form.create({ name: 'validate_other' })(Demo);
export default WrappedDemo2;
