import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import { enquireScreen } from 'enquire-js';
import Header from './Layout/Nav0';
import Footer from './Layout/Footer1';
import Home from './Home';
import AIResearch from './AIResearch';
import AINews from  './AINews'
import AILab from  './AILab'

import {
  Nav00DataSource,
  Footer10DataSource,
} from './Home/data.source.js';

let isMobil;
enquireScreen((b)=>{
  isMobil=b;
});

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      isMobil,
    };
  }
  componentDidMount() {
    //适配手机屏幕
    enquireScreen((b)=>{
      this.setState({isMobil: !!b});
    });
  }
  render() {
    return(
        <Router>
          <div>
            <Header dataSource={Nav00DataSource} isMobile={this.isMobile} />
            <Route exact path="/" component={Home} />
            <Route exact path="/AIResearch" component={AIResearch}/>
            <Route exact path="/AINews" component={AINews}/>
            <Route exact path="/AILab" component={AILab}/>
            <Footer dataSource={Footer10DataSource} isMobile={this.isMobile} />
          </div>
        </Router>
    );
  }
}


export default App;
