import React from "react";
import axios from 'axios';

import {
    Form,
    Select,
    InputNumber,
    Switch,
    Radio,
    Slider,
    Button,
    Upload,
    Icon,
    Rate,
    Checkbox,
    Row,
    Col,
    Card,
    Modal,
} from 'antd';

//定义后台返回数据类型
let responseData={
    url:'https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png'
};

const { Option } = Select;
const { Meta } = Card;

function getBase64(file) {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });
}

class Demo extends React.Component {
    state = {
        previewVisible: false,
        previewImage: '',
        fileList: [

        ],
    };

    //提交事件
    //图片单独提交，submit只是为了请求后台数据
    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
            let data = new FormData();
            data.append("upload",values.upload);
            let url = "";
            axios.post(url, data,{
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            })
                .then(function (response) {
                    //得到后台返回的分数值
                    responseData=response.data;
                    console.log(responseData.url);
                })
                .catch(function (error) {
                    console.log(error);
                });
        });
    };

    //图片预览
    handlePreview = async file => {
        if (!file.url && !file.preview) {
            file.preview = await getBase64(file.originFileObj);
        }

        this.setState({
            previewImage: file.url || file.preview,
            previewVisible: true,
        });
    };

    //关闭预览框
    handleCancel = () => this.setState({ previewVisible: false });

    handleChange = ({ fileList }) => this.setState({ fileList });

    normFile = e => {
        console.log('Upload event:', e);
        if (Array.isArray(e)) {
            return e;
        }
        return e && e.fileList;
    };

    render() {
        const { previewVisible, previewImage, fileList } = this.state;
        const { getFieldDecorator } = this.props.form;
        const uploadButton = (
            <div>
                <Icon type="plus" />
                <div className="ant-upload-text">Upload</div>
            </div>
        );
        const formItemLayout = {
            labelCol: { span: 6 },
            wrapperCol: { span: 14 },
        };
        return (
            <Form {...formItemLayout} onSubmit={this.handleSubmit}>
                <Form.Item label="选择图片">
                    {
                        getFieldDecorator('upload',{
                            valuePropName: 'fileList',
                            getValueFromEvent: this.normFile,
                        })(
                            <Upload
                                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                listType="picture-card"
                                fileList={fileList}
                                onPreview={this.handlePreview}
                                onChange={this.handleChange}
                            >
                                {fileList.length >= 2 ? null : uploadButton}
                            </Upload>
                        )
                    }
                    <Modal visible={previewVisible} footer={null} onCancel={this.handleCancel}>
                        <img alt="example" style={{ width: '100%' }} src={previewImage} />
                    </Modal>
                </Form.Item>

                <Form.Item wrapperCol={{ span: 12, offset: 6 }}>
                    <Button type="primary" htmlType="submit">
                        开始！
                    </Button>
                </Form.Item>
                <div style={{ background: '#ECECEC', padding: '30px' }}>
                    <Card
                        hoverable
                        style={{ width: 240,marginLeft:550}}
                        cover={<img alt="example" src={responseData.url} />}
                    >
                        <Meta title="处理结果" style={{textAlign:"center"}}/>
                    </Card>
                </div>
            </Form>
        );
    }
}

const WrappedDemo1 = Form.create({ name: 'validate_other' })(Demo);
export default WrappedDemo1;
